<?php defined('SYSPATH') or die('No direct script access.');class Controller_Cms_Ajax_Widgets_Popularcontent extends Controller {		public function action_get()	{		$content = ORM::factory('Content')			->order_by('hits', 'DESC')			->limit(10)			->find_all();		$contents = array();		if((bool)$content->count())		{			foreach($content as $c)			{				$contents[] = array(					'id' => $c->id,					'title' => $c->title,					'type' => $c->contenttype->display,					'hits' => $c->hits				);			}		}		ajax::success('',array(			'contents' => $contents		));	}	}