<?php defined('SYSPATH') or die('No direct script access.');class Controller_Cms_Ajax_Widgets_Sitestats extends Controller {		public function action_get()	{		$newusers = ORM::factory('User')			->where('created','>',time()-(60*60*24))			->count_all();		$pages = ORM::factory('Page')			->where('type','=', 'page')			->count_all();		ajax::success('ok', array(			'newusers' => $newusers,			'pages' => $pages		));	}	}