<?php defined('SYSPATH') or die('No direct script access.');class Controller_Cms_Super extends Controller_Cms {		public function action_index()	{		$view = View::factory('Cms/Super/index');		reply::ok($view, 'files', array(			'viewModel' => 'viewModels/Super/index'		));	}		public function action_contenttypes()	{		$view = View::factory('Cms/Super/contenttypes');		reply::ok($view, 'files', array(			'viewModel' => 'viewModels/Super/contenttypes'		));	}		public function action_menus()	{		$view = View::factory('Cms/Super/menus');		reply::ok($view, 'files', array(			'viewModel' => 'viewModels/Super/menus'		));	}		public function action_options()	{		$optiongroups = ORM::factory('Optiongroup')->find_all();		$grouparr = array();		if((bool)$optiongroups->count())		{			foreach ($optiongroups as $group)			{				$info = $group->info();				$info['options'] = array();				$options = $group->options->find_all();				if((bool)$options->count())				{					foreach($options as $option)					{						$info['options'][] = $option->info();					}				}
				$grouparr[] = $info;
			}		}				$view = View::factory('Cms/Super/options');		reply::ok($view, 'files', array(			'viewModel' => 'viewModels/Super/options',			'groups' => $grouparr		));	}	}