<?php defined('SYSPATH') or die('No direct script access.');class Model_Blocktype_Meta extends ORM {		protected $_belongs_to = array(		'blocktype' => array(),	);		public function info()	{		$val = $this->value;		if(strpos($val,','))		{			$val = explode(',', $val);		}		return array(			'id' => $this->id,			'blocktype_id' => $this->blocktype_id,			'key' => $this->key,			'values' => $val		);	}		public function copy($blocktype_id = false)	{		$new = ORM::factory('Blocktype_Meta');		if($blocktype_id)		{			$new->blocktype_id = $blocktype_id;		}		else		{			$new->blocktype_id = $this->blocktype_id;		}		$new->key = $this->key;		$new->value = $this->value;		$new->save();		return $new;	}	}