<?php defined('SYSPATH') or die('No direct script access.');class Model_Category extends Model_Stuffwithslugs {		protected $_belongs_to = array(		'contenttype' => array()	);		protected $_has_many = array(		'contents' => array('through' => 'categories_content')	);		protected $_sorting = array(		'title' => 'ASC'	);		public function save(Validation $validation = NULL)	{		$this->checkslug();		$this->set_guid();		return parent::save($validation);	}	}