<?php defined('SYSPATH') or die('No direct script access.');class Model_File_Version extends ORM {		protected $_belongs_to = array(		'file' => array()	);		public function delete()	{		try		{			unlink($this->file->path.$this->filename);		}		catch(exception $e)		{			// erh..		}		return parent::delete();	}		public function filename()	{		$fileparts = explode('/', $this->filename);		return end($fileparts);	}		public function info()	{		return array(			'id' => $this->id,			'file_id' => $this->file_id,			'title' => $this->title(),			'filename' => $this->filename,			'width' => $this->width,			'height' => $this->height,			'time' => $this->time		);	}		public function title()	{		if(empty($this->title))		{			return $this->width.' x '.$this->height;		}		return $this->title.' ('.$this->width.' x '.$this->height.')';	}	}