<?php defined('SYSPATH') or die('No direct script access.');class Model_Widget extends ORM {		protected $_belongs_to = array(		'widgettype' => array()	);		protected $_sorting = array(		'order' => 'ASC'	);		public function info()	{		return array(			'id' => $this->id,			'widgettype' => $this->widgettype->info(),			'dashboard_id' => $this->dashboard_id,			'order' => $this->order,			'data' => $this->data		);	}	}