<?php defined('SYSPATH') or die('No direct script access.');abstract class content {		public static function available($contenttype_id)	{		$contenttypetypes = ORM::factory('Contenttypetype')->where('contenttype_id','=',$contenttype_id)->find_all();		$returner = array();		if((bool)$contenttypetypes->count())		{			foreach($contenttypetypes as $contenttypetype)			{				$numpages = ORM::factory('Content')->where('contenttypetype_id', '=', $contenttypetype->id)->count_all();				if($numpages < $contenttypetype->max || $contenttypetype->max == 0)				{					$returner[$contenttypetype->id] = array();					$returner[$contenttypetype->id]['id'] = $contenttypetype->id;					$returner[$contenttypetype->id]['contenttypeid'] = $contenttypetype->contenttype_id;					$returner[$contenttypetype->id]['display_name'] = $contenttypetype->display;				}			}		}		return $returner;	}	}	