<?phpabstract class filehelper {		public static function tableoutput($file)	{		$html = '<tr>';		$html .= '<td><a href="#" data-fileid="'.$file->id.'" data-fileid="'.$file->id.'" class="modaltoggler '.($file->is_image()?'img':'file').'">';		if($file->is_image())		{			$html .= '<img class="img-polaroid" src="'.$file->get(75,75).'" />';		}		else		{			$html .= '<img src="'.$file->get_icon().'" />';		}		$html .= '</a></td>';		$html .= '<td><input type="text" value="'.$file->filename().'" /></td>';		$html .= '<td>'.$file->size().'</td>';		$html .= '</tr>';		return $html;	}	}