<div class="complex" data-blockid="<?php echo $block->id; ?>">	<div>		<div class="form-inline">			<select class="blocktypes form-control" style="vertical-align: top;">				<option value="0">Vælg indholdstype</option><?php				$blocktypes = ORM::factory('Blocktype')					->where('parent','=',$block->blocktype_id)					->find_all();				if((bool)$blocktypes->count())				{					foreach($blocktypes as $blocktype)					{						$numblocks = ORM::factory('block')->where('content_id','=',$block->content_id)->where('blocktype_id','=',$blocktype->id)->count_all();						echo '<option value="'.$blocktype->id.'" data-max="'.$blocktype->max.'" class="'.(($blocktype->max>0&&($numblocks>=$blocktype->max))?'hide':'').'">'.$blocktype->display.'</option>';					}				}?>			</select>			<button class="btn btn-primary blocktypes-btn" data-contentid="<?php echo $block->content_id; ?>" data-bind="click:addBlock">				<span class="glyphicon glyphicon-plus"></span> Tilføj			</button>		</div>		<hr />	</div>	<div class="kids" data-bind="sortable:sortChildBlocks()"></div></div>