<?php	$contenttype = ORM::factory('Contenttype', $block->blocktype->meta('contenttype'));	$content = $contenttype->contents->find_all();?><select class="form-control" name="block[<?php echo $block->id; ?>][]"<?php echo ((bool)$block->blocktype->meta('multiple')?' multiple="multiple"':'') ?>>	<option value="0"<?php echo ($block->value==0||$block->value==''?' selected="selected"':''); ?>>VÃ¦lg indhold</option><?php	if((bool)$content->count())	{		$selected = array();		if(is_numeric($block->value))		{			$selected[] = $block->value;		}		if(!is_numeric($block->value) && !empty($block->value))		{			$selected = json_decode($block->value);		}		foreach($content as $page)		{			echo '<option value="'.$page.'"'.(in_array($page->id, $selected)?' selected="selected"':'').'>'.$page->title().'</option>';		}	}?></select>