<select name="block[<?php echo $block->id; ?>][]" class="span10">	<option value="0">VÃ¦lg produkt...</option><?php	$products = ORM::factory('Product')->find_all();	$values = json_decode($block->value);	if((bool)$products->count())	{		foreach($products as $product)		{			$selected = false;			if(is_array($values) && in_array($product->id, $values))			{				$selected = true;			}			echo '<option value="'.$product->id.'"'.($selected?' selected="selected"':'').'>'.$product->title().' ('.$product->brand->title().')</option>';		}	}?></select>