<?php	$limit = 10;	$numfiles = ORM::factory('File')->count_all();	$currentpage = (int)arr::get($_GET, 'page', 1);	$numpages = ceil($numfiles/$limit);	$pagination = '';	if(!is_numeric($currentpage))	{		$currentpage = 1;	}	else	{		if($currentpage > $numpages)		{			$currentpage = $numpages;		}		if($currentpage < 1)		{			$currentpage = 1;		}	}	if($numfiles > $limit)	{		for($i=1;$i<=$numpages;$i++)		{			$pagination .= '<li class="' . ($i == $currentpage?'active':'') . '"><a href="#/files?page='.$i.'">'.$i.'</a></li>';		}	}?><div class="page-header">	<h1>Filer</h1></div><div class="row" id="view-files">	<div class="col-xs-9">				<div class="form-inline">			<div class="row">				<div class="col-xs-6 options">					<select class="form-control massactions">						<option value="0">Handlinger</option>						<option value="delete">Slet</option>					</select>					<button type="button" class="btn btn-default deletebtn">Udfør</button>				</div>				<div class="col-xs-6 text-right">					<ul class="pagination" data-bind="foreach:pages">						<li class="" data-bind="css{active:current()}">							<a href="#" data-bind="text:number,click:$root.switchPage"></a>						</li>					</ul>				</div>			</div>		</div>				<table class="table table-striped" id="files">			<thead>				<th><input class="checkall" type="checkbox" /></th>				<th></th>				<th>Størrelse</th>				<th>Sti</th>				<th>Titel</th>			</thead>			<tfoot>				<th><input class="checkall" type="checkbox" /></th>				<th></th>				<th>Størrelse</th>				<th>Sti</th>				<th>Titel</th>			</tfoot>			<tbody data-bind="foreach:files">				<tr>					<td><input type="checkbox" class="filecheck" data-bind="value:id" /></td>					<td class="file-thumbnail">						<a href="#" data-bind="attr:{href:'#/files/edit/'+id}" title="Rediger">							<img data-bind="attr:{src:thumb}" src="" />						</a>					</td>					<td>						<div class="control-group">							<?php /*<div class="control